mapper.select:
	
	select x.rnum, x.*
	from(
    	select rownum as rnum, A.*
    	from(
        	select m.user_nm, m.authority_no, e.etp_email, e.user_id, e.etp_addr, e.etp_ph_no, e.etp_license_no, e.etp_nm, e.etp_content
        	from member m, enterprise e
        	where m.user_id = e.user_id and m.authority_no = 3
    	) A
    	where rownum < ${endNum}
	) x
	where x.rnum >= ${startNum}

	
	select x.rnum, x.*
	from (
    	select rownum as rnum, A.*
        	from (select d.user_id, m.user_nm, m.ph_no, d.d_gender, d.d_dt, d.d_addr, d.d_kind, d.d_nm, d.d_content
            	from dogs d, member m where d.user_id = m.user_id
        	) A
            	where rownum < ${endNum}
	) x
	where x.rnum >= ${startNum}

mapper.update:
update member
set authority_no = 2
where user_id = #{user_id}

mapper.select:
	
		select x.rnum, x.*
		from (select rownum as rnum, A.*
    	from (select * from member) A
    	where rownum < ${endNum}
		) x
		where x.rnum >= ${startNum}
