mapper.update:
update enterprise set etp_email = #{etp_email},
etp_addr = #{etp_addr},
etp_ph_no = #{etp_ph_no},
etp_license_no = #{etp_license_no},
etp_nm = #{etp_nm}
where etp_cd = #{etp_cd}

update enterprise_info set etp_if_info = #{etp_if_info},
etp_if_intro = #{etp_if_intro},
etp_if_time1 = #{time1},
etp_if_time2 = #{time2}
where etp_cd = #{etp_cd}
